import { useState, useEffect } from "react";
import { postScore } from "../api";

export default function Game1() {
  const [targetNumber, setTargetNumber] = useState(0);
  const [guess, setGuess] = useState("");
  const [attempts, setAttempts] = useState(0);
  const [message, setMessage] = useState("Pick a number between 1 and 100");
  const [isGameOver, setIsGameOver] = useState(false);

  // Initialize game
  const initGame = () => {
    setTargetNumber(Math.floor(Math.random() * 100) + 1);
    setGuess("");
    setAttempts(0);
    setMessage("Pick a number between 1 and 100");
    setIsGameOver(false);
  };

  useEffect(() => {
    initGame();
  }, []);

  const handleGuess = async (e) => {
    e.preventDefault(); // Prevents page refresh if inside a form
    if (isGameOver) return;

    const g = parseInt(guess);
    if (isNaN(g)) return setMessage("Please enter a valid number!");

    const newAttempts = attempts + 1;
    setAttempts(newAttempts);

    if (g === targetNumber) {
      setMessage(`ðŸŽ‰ Correct! It was ${targetNumber}.`);
      setIsGameOver(true);
      try {
        await postScore({ username: "Player1", game: "Game1", score: Math.max(0, 100 - newAttempts) });
      } catch (err) {
        console.error("API Error:", err);
      }
    } else {
      setMessage(g < targetNumber ? "ðŸ“ˆ Too low! Try again." : "ðŸ“‰ Too high! Try again.");
      setGuess(""); // Clear input for next guess
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md text-center">
        <h2 className="text-3xl font-bold text-indigo-600 mb-2">Number Master</h2>
        <p className="text-gray-500 mb-6">{message}</p>

        <form onSubmit={handleGuess} className="space-y-4">
          <input
            type="number"
            value={guess}
            disabled={isGameOver}
            onChange={(e) => setGuess(e.target.value)}
            className="w-full text-center text-2xl p-3 border-2 border-indigo-100 rounded-lg focus:outline-none focus:border-indigo-500 transition-colors"
            placeholder="??"
            autoFocus
          />

          {!isGameOver ? (
            <button
              type="submit"
              className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 rounded-lg shadow-lg transition-transform active:scale-95"
            >
              Submit Guess
            </button>
          ) : (
            <button
              onClick={initGame}
              className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-3 rounded-lg shadow-lg transition-transform active:scale-95"
            >
              Play Again
            </button>
          )}
        </form>

        <div className="mt-6 pt-6 border-t border-gray-100 flex justify-around text-sm font-medium text-gray-400">
          <div>Attempts: <span className="text-indigo-600">{attempts}</span></div>
          <div>Best Score: <span className="text-indigo-600">--</span></div>
        </div>
      </div>
    </div>
  );
}
