import { useState, useEffect } from "react";
import { postScore } from "../api";
import "./App.css"; // Ensure your CSS is imported

export default function Game1() {
  const [target, setTarget] = useState(0);
  const [guess, setGuess] = useState("");
  const [attempts, setAttempts] = useState(0);
  const [message, setMessage] = useState("Guess a number between 1-100");
  const [isWon, setIsWon] = useState(false);

  // Initialize or Reset Game
  const startNewGame = () => {
    setTarget(Math.floor(Math.random() * 100) + 1);
    setGuess("");
    setAttempts(0);
    setMessage("New game started! Good luck.");
    setIsWon(false);
  };

  useEffect(() => {
    startNewGame();
  }, []);

  const handleGuess = async () => {
    if (isWon) return;

    const numGuess = parseInt(guess);
    if (isNaN(numGuess)) return setMessage("Enter a number!");

    const currentAttempts = attempts + 1;
    setAttempts(currentAttempts);

    if (numGuess === target) {
      setMessage(`ðŸŽ¯ Correct! ${currentAttempts} attempts.`);
      setIsWon(true);
      try {
        await postScore({ username: "Player1", game: "Game1", score: 100 - currentAttempts });
      } catch (err) {
        console.error("Score Error:", err);
      }
    } else {
      setMessage(numGuess < target ? "Too Low! ðŸ“ˆ" : "Too High! ðŸ“‰");
      setGuess("");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="game-card">
          <h2 style={{ color: "#61dafb", marginTop: 0 }}>Number Quest</h2>

          <p className="status-message">{message}</p>

          <div style={{ margin: "20px 0" }}>
            <input
              className="game-input"
              type="number"
              value={guess}
              onChange={(e) => setGuess(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleGuess()}
              disabled={isWon}
              autoFocus
            />
          </div>

          {!isWon ? (
            <button className="game-button" onClick={handleGuess}>
              GUESS
            </button>
          ) : (
            <button className="game-button" style={{ backgroundColor: "#4caf50" }} onClick={startNewGame}>
              PLAY AGAIN
            </button>
          )}

          <div style={{ marginTop: "20px", fontSize: "0.9rem", opacity: 0.7 }}>
            Attempts: {attempts}
          </div>
        </div>
      </header>
    </div>
  );
}
